mapper.update:
UPDATE car_owner SET
status = #{status,jdbcType=INTEGER}
where car_owner_id = #{carOwnerId,jdbcType=INTEGER}

mapper.select:
select
from car_owner where status = 1 and month(birthday) = month(curdate()) and day(birthday) = day(curdate()) and (sms_time is NULL or DATE_FORMAT(sms_time,'%Y-%m-%d') != curdate())
and car_dealer_id > 0
and car_dealer_id = #{carDealerId,jdbcType=INTEGER}

mapper.update:
UPDATE car_owner SET
sms_time = #{smsTime,jdbcType=TIMESTAMP}
where car_owner_id = #{carOwnerId,jdbcType=INTEGER}
